import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';

function AboutUs() {
  // Refs for the elements you want to animate
  const containerRef = useRef(null);
  const imgRef = useRef(null);
  const descRef = useRef(null);

  useEffect(() => {
    // GSAP animations
    const timeline = gsap.timeline();

    timeline
      .from(containerRef.current, {
        opacity: 0,
        duration: 1,
        ease: "power2.out",
      })
      .from(imgRef.current, {
        x: -100,
        opacity: 0,
        duration: 1,
        ease: "power2.out",
      }, "<") // Start this animation with the previous one
      .from(descRef.current, {
        x: 100,
        opacity: 0,
        duration: 1,
        ease: "power2.out",
      }, "-=0.5"); // Overlap animations by 0.5 seconds
  }, []);

  return (
    <div className="container" ref={containerRef}>
      <div className="SecImg" ref={imgRef}>
        <img src="mysor_\public\aboutus.jpeg" alt="img1" />
      </div>
      <div className="desc" ref={descRef}>
        <p>OUR STORY</p>
        <h2>
          Driving Quality, Powering Performance
          Your trusted source for top-grade motor and vehicle parts, built for reliability and excellence.
        </h2>
        <p>
          Explore a wide range of top-quality motor and vehicle components designed to ensure unmatched performance and durability.
        </p>
        <div className="learn-more">
          <button>Learn More</button>
        </div>
      </div>
    </div>
  );
}

export default AboutUs;
