import React, { useEffect, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';


const AboutUs = () => {
  const aboutRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.3 } // Trigger when 30% of the section is visible
    );

    const elements = aboutRef.current.querySelectorAll('.fade-in');
    elements.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <section className="about-us-section" ref={aboutRef}>
      {/* Social Media Links */}
      <div className="social-links">
        <a href="https://www.instagram.com" target="_blank" rel="noopener noreferrer" className="social-icon instagram">
          <i className="fab fa-instagram"></i>
        </a>
        <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" className="social-icon facebook">
          <i className="fab fa-facebook-f"></i>
        </a>
        <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer" className="social-icon linkedin">
          <i className="fab fa-linkedin-in"></i>
        </a>
      </div>

      {/* Main Content */}
      <div className="overlay">
        <h1 className="section-title fade-in">About Us</h1>
        <p className="section-description fade-in">
          <span className="company-name">Mysore Motors</span> is a leading provider of premium-quality vehicle components and mechanical parts. 
          We bring decades of expertise, serving industries and automotive enthusiasts with precision-engineered solutions.
        </p>
        <p className="company-details fade-in">
          Our mission is to ensure **top-tier performance**, reliability, and durability for all your motor and vehicle needs.
        </p>
      </div>
    </section>
  );
};

export default AboutUs;
