import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';

function AboutUs() {
  const containerRef = useRef(null);
  const imgRef = useRef(null);
  const descRef = useRef(null);

  useEffect(() => {
    const timeline = gsap.timeline();

    timeline
      .to(containerRef.current, {
        opacity: 1,
        duration: 1,
        ease: "power2.out",
      })
      .to(imgRef.current, {
        x: 0,
        opacity: 1,
        duration: 1,
        ease: "power2.out",
      }, "<")
      .to(descRef.current, {
        x: 0,
        opacity: 1,
        duration: 1,
        ease: "power2.out",
      }, "-=0.5");
  }, []);

  return (
    <div className="container" ref={containerRef}>
      <div className="SecImg" ref={imgRef}>
        <img src="/aboutus.jpeg" alt="About Us" />
      </div>
      <div className="desc" ref={descRef}>
        <p>OUR STORY</p>
        <h2>Driving Quality, Powering Performance</h2>
        <p>Explore a wide range of top-quality motor and vehicle components designed to ensure unmatched performance and durability.</p>
        <div className="learn-more">
          <button>Learn More</button>
        </div>
      </div>
    </div>
  );
}

export default AboutUs;
