import React, { useState, useEffect } from "react";

const Home = () => {
  const [typedText, setTypedText] = useState(""); // For the typing effect
  const [isTypingComplete, setIsTypingComplete] = useState(false); // For subheading visibility
  const headingText = "Welcome to Mysore Motors – Where Performance Meets Precision";

  useEffect(() => {
    let i = 0;

    const typeEffect = () => {
      if (i < headingText.length) {
        setTypedText((prev) => prev + headingText.charAt(i));
        i++;
        setTimeout(typeEffect, 50); // Adjust typing speed here
      } else {
        setIsTypingComplete(true); // Typing is complete
      }
    };

    typeEffect();
  }, [headingText]);

  return (
    <div className="home">
      <h2>{typedText}</h2>
      {isTypingComplete && (
        <>
          <h3>
            Explore a World of Quality Motor and Vehicle Parts, Engineered to
            Keep You Ahead on Every Journey.
          </h3>
          <a href="#explore" className="hero-button">
            Explore Now
          </a>
        </>
      )}
    </div>
  );
};

export default Home;
